import React, { Component } from 'react';
import { Redirect, Route, Link, Switch } from 'react-router-dom'
import HomePage from './Pages/HomePage/'
import SubmitPage from './Pages/SubmitPage'
import ComfirmPage from './Pages/ComfirmPage'

import './App.css';

let postURL

class App extends Component {
  constructor ( props ) {
    super( props )
    this.state = {
      email: '',
      redirectToReferrer: false
    }
    this.onFormChange = this.onFormChange.bind( this );
    this.onFormChange = this.onFormChange.bind( this );
  }

  onFormChange = ( event ) => {
    const { email, value } = event.target;
    this.setState( {
      [ email ]: value
    } )
    // this.props.history.push( '/submit' );
  }

  onFormSubmit( e ) {
    e.preventDefault();
    console.log( 'you info was submitted' )
    this.setState( {
      email: this.state.value,
      redirectToReferrer: true
    } )
    const redirectToReferrer = this.state.redirectToReferrer;
    if ( redirectToReferrer === true ) {
      <Redirect to="/submit" />
    }
  }

  componentDidMount() {
    postURL = `${ global.location.origin }/submit`;
  }

  render() {

    return <div>
      <div className='nav'></div>
      <div>
        <Switch>
          <Route exact path='/' render={ props => <HomePage { ...props } handleChange={ this.onFormChange } handleSubmit={ this.onFormSubmit } /> } />
          <Route path='/submit' render={ props => <SubmitPage /> } />
          <Route path='/comfirm' render={ props => <ComfirmPage /> } />
        </Switch>
      </div>
    </div>
  }
}

export default App;
